import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';

function Map({ farmers }) {
  const position = [46.227638, 2.213749]; // Centre de la France

  return (
    <MapContainer center={position} zoom={6} style={{ height: '400px', width: '100%' }}>
      <TileLayer
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
      />
      {farmers.map(farmer => (
        <Marker
          key={farmer.id}
          position={[farmer.location.coordinates[1], farmer.location.coordinates[0]]}
        >
          <Popup>
            {farmer.name}<br />
            <a href={`/farmers/${farmer.id}`}>Voir les d√©tails</a>
          </Popup>
        </Marker>
      ))}
    </MapContainer>
  );
}

export default Map;
