import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.png';
import axios from 'axios';
import FarmerList from './FarmerList';
import Map from './Map';

// Configuration de l'URL de base de l'API
const API_BASE_URL = 'http://localhost:3000'; // À ajuster selon votre configuration

function HomePage() {
  const [city, setCity] = useState('');
  const [useGeolocation, setUseGeolocation] = useState(false);
  const [radius, setRadius] = useState('50');
  const [farmers, setFarmers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [coordinates, setCoordinates] = useState(null);

  const searchContainerStyle = {
    border: '1px solid #ccc',
    borderRadius: '10px',
    padding: '20px',
    margin: '20px auto',
    width: '300px',
    boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
  };

  const inputStyle = {
    margin: '8px 0',
    padding: '8px',
    width: '100%',
    borderRadius: '4px',
    border: '1px solid #ddd'
  };

  const buttonStyle = {
    padding: '10px 20px',
    border: 'none',
    borderRadius: '20px',
    cursor: 'pointer',
    textDecoration: 'none',
    color: 'white',
    backgroundColor: '#4CAF50',
    margin: '10px',
    fontWeight: 'bold',
    transition: 'background-color 0.3s'
  };

  const handleGeolocation = () => {
    if (navigator.geolocation) {
      setLoading(true);
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setCoordinates({
            latitude: position.coords.latitude,
            longitude: position.coords.longitude
          });
          // Recherche inversée pour obtenir la ville à partir des coordonnées
          reverseGeocode(position.coords.latitude, position.coords.longitude);
          setLoading(false);
        },
        (error) => {
          console.error("Erreur de géolocalisation:", error);
          setError("Impossible d'obtenir votre position. Veuillez entrer une ville manuellement.");
          setLoading(false);
          setUseGeolocation(false);
        }
      );
    } else {
      setError("La géolocalisation n'est pas prise en charge par votre navigateur.");
      setUseGeolocation(false);
    }
  };

  const reverseGeocode = async (lat, lon) => {
    try {
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lon}`
      );
      if (response.data && response.data.address) {
        const address = response.data.address;
        // Prioriser la ville, sinon prendre town, village ou hamlet
        const location = address.city || address.town || address.village || address.hamlet;
        setCity(location);
      }
    } catch (error) {
      console.error("Erreur lors de la géolocalisation inversée:", error);
    }
  };

  const handleSearch = async () => {
    if (!city && !coordinates) {
      setError("Veuillez entrer une ville ou utiliser la géolocalisation");
      return;
    }

    if (radius <= 0) {
      setError("Le rayon doit être supérieur à 0");
      return;
    }

    setLoading(true);
    setError(null);

    try {
      let url;
      if (coordinates && useGeolocation) {
        // Utiliser les coordonnées pour la recherche
        url = `${API_BASE_URL}/farmers?latitude=${coordinates.latitude}&longitude=${coordinates.longitude}&radius=${radius}`;
      } else {
        // Utiliser la ville pour la recherche
        url = `${API_BASE_URL}/farmers?city=${encodeURIComponent(city)}&radius=${radius}`;
      }

      const response = await axios.get(url);
      console.log("Données reçues:", response.data);

      if (response.data && Array.isArray(response.data)) {
        setFarmers(response.data);
        if (response.data.length === 0) {
          setError("Aucun agriculteur trouvé dans cette zone");
        }
      } else {
        throw new Error("Format de données incorrect");
      }
    } catch (error) {
      console.error("Erreur lors de la recherche:", error);
      setError(`Erreur lors de la recherche: ${error.response?.data || error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Effet pour gérer le changement d'état de la case à cocher de géolocalisation
  React.useEffect(() => {
    if (useGeolocation) {
      handleGeolocation();
    } else {
      setCoordinates(null);
    }
  }, [useGeolocation]);

  return (
    <div style={{ textAlign: 'center', maxWidth: '1200px', margin: '0 auto', padding: '20px' }}>
      <img src={logo} alt="Logo Save Agri" style={{ width: '200px', marginBottom: '20px' }} />

      <div style={searchContainerStyle}>
        <h2 style={{ marginTop: '0', color: '#4CAF50' }}>Trouvez des agriculteurs locaux</h2>

        <div>
          <label htmlFor="city">Ville:</label>
          <input
            type="text"
            id="city"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            disabled={useGeolocation && coordinates}
            style={inputStyle}
            placeholder="Entrez une ville"
          />
        </div>

        <div style={{ display: 'flex', alignItems: 'center', margin: '10px 0' }}>
          <input
            type="checkbox"
            id="useGeolocation"
            checked={useGeolocation}
            onChange={(e) => setUseGeolocation(e.target.checked)}
            style={{ marginRight: '10px' }}
          />
          <label htmlFor="useGeolocation">Utiliser ma position actuelle</label>
        </div>

        <div>
          <label htmlFor="radius">Rayon de recherche (km):</label>
          <input
            type="number"
            id="radius"
            value={radius}
            onChange={(e) => setRadius(e.target.value)}
            style={inputStyle}
            min="1"
            max="200"
          />
        </div>

        <button 
          onClick={handleSearch} 
          style={buttonStyle}
          disabled={loading}
        >
          {loading ? 'Recherche en cours...' : 'Rechercher'}
        </button>

        {error && (
          <div style={{ color: 'red', margin: '10px 0' }}>
            {error}
          </div>
        )}
      </div>

      {farmers.length > 0 && (
        <div style={{ marginTop: '30px' }}>
          <Map farmers={farmers} />
          <FarmerList farmers={farmers} />
        </div>
      )}

      <div style={{ marginTop: '30px', borderTop: '1px solid #eee', paddingTop: '20px' }}>
        <h3>Connectez-vous ou créez un compte</h3>
        <div>
          <Link to="/login/user" style={{ ...buttonStyle, backgroundColor: '#2196F3' }}>Je suis un utilisateur</Link>
          <Link to="/login/farmer" style={{ ...buttonStyle, backgroundColor: '#FF9800' }}>Je suis un agriculteur</Link>
        </div>
      </div>
    </div>
  );
}

export default HomePage;
