// save-agri-frontend/src/components/FarmerPage.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import api from '../services/api'; // Importez votre instance Axios
import './FarmerPage.css'; // Importez le fichier CSS

function FarmerPage() {
    const { id } = useParams(); // Récupère l'ID depuis l'URL
    const [farmer, setFarmer] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchFarmer = async () => {
            try {
                const response = await api.get(`/farmers/${id}`);
                setFarmer(response.data);
                console.log(response.data); // Pour voir les données dans la console
            } catch (err) {
                setError(err.message || 'Erreur lors du chargement des détails du producteur.');
                console.error("Erreur lors de la récupération des données:", err);
            } finally {
                setLoading(false);
            }
        };

        fetchFarmer();
    }, [id]);

    if (loading) {
        return <div>Chargement des informations du producteur...</div>;
    }

    if (error) {
        return <div>Erreur: {error}</div>;
    }

    if (!farmer) {
        return <div>Producteur non trouvé.</div>;
    }

    return (
        <div className="farmer-page-container">
            <h1>{farmer.name}</h1>
            <div className="farmer-details">
                <img src={farmer.photo} alt={farmer.name} className="farmer-photo" />
                <div className="farmer-info">
                    <p>{farmer.description}</p>
                    <p>Adresse: {farmer.address}, {farmer.city}, {farmer.zip_code}</p>
                    <p>Téléphone: {farmer.phone}</p>
                    <p>Site web: <a href={farmer.website}>{farmer.website}</a></p>
                    {/* Ajoutez ici d'autres informations que vous souhaitez afficher */}
                </div>
            </div>
        </div>
    );
}

export default FarmerPage;
