import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Map from './Map';
import FarmerList from './FarmerList';
import axios from 'axios';
import logo from '../assets/logo.png'; // Importe le logo

function HomePage() {
  const [farmers, setFarmers] = useState([]);

  useEffect(() => {
    // Récupérer la liste des producteurs depuis l'API back-end
    axios.get('/farmers')
      .then(response => {
        setFarmers(response.data);
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des producteurs', error);
      });
  }, []);

  const buttonStyle = {
    fontSize: '1.2em',
    padding: '10px 20px',
    margin: '0 20px',
    backgroundColor: '#4CAF50',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    textDecoration: 'none' // Pour enlever le soulignement des liens
  };

  return (
    <div style={{ textAlign: 'center' }}> {/* Centre le contenu horizontalement */}
      <img src={logo} alt="Logo Save Agri" style={{ width: '200px' }} /> {/* Affiche le logo */}
      <div>
        <Link to="/login/user">I'm User</Link>
        <Link to="/login/farmer">I'm Farmer</Link>
      </div>
      <Map farmers={farmers} />
      <FarmerList farmers={farmers} />
    </div>
  );
}

export default HomePage;
