import React, { useState} from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/logo.png';
import axios from 'axios';


function HomePage() {
  const [city, setCity] = useState('');
  const [useGeolocation, setUseGeolocation] = useState(false);
  const [radius, setRadius] = useState('');
  const [farmers, setFarmers] = useState([]);


  const searchContainerStyle = {
    border: '1px solid black',
    borderRadius: '10px',
    padding: '20px',
    margin: '20px auto',
    width: '300px',
  };

  const inputStyle = {
    margin: '5px 0',
  };

  const buttonStyle = {
    padding: '10px 20px',
    border: '1px solid black',
    borderRadius: '20px',
    cursor: 'pointer',
    textDecoration: 'none',
    color: 'black',
    backgroundColor: 'white',
    margin: '10px',
  };

  const handleSearch = async () => {
    try {
      // Envoie une requête à l'API backend avec les paramètres de recherche
      const response = await axios.get(`/farmers?city=${city}&radius=${radius}`);
      setFarmers(response.data);
    } catch (error) {
      console.error("Erreur lors de la recherche: ", error);
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <img src={logo} alt="Logo Save Agri" style={{ width: '200px' }} />

      <div style={searchContainerStyle}>
        <div>
          <label htmlFor="city">Enter a city:</label>
          <input
            type="text"
            id="city"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            style={inputStyle}
          />
        </div>
        <div>
          <label htmlFor="useGeolocation">or located:</label>
          <input
            type="checkbox"
            id="useGeolocation"
            checked={useGeolocation}
            onChange={(e) => setUseGeolocation(e.target.checked)}
            style={inputStyle}
          />
        </div>
        <div>
          <label htmlFor="radius">Enter the radius:</label>
          <input
            type="number"
            id="radius"
            value={radius}
            onChange={(e) => setRadius(e.target.value)}
            style={inputStyle}
          />
        </div>
        <button onClick={handleSearch} style={buttonStyle}>Search</button>
      </div>

      {/* Afficher les résultats de la recherche */}
      {farmers.map(farmer => (
        <div key={farmer.id}>
          <h3>{farmer.name}</h3>
          <p>{farmer.description}</p>
          {/* Afficher d'autres informations sur le farmer */}
        </div>
      ))}

      <div>
        <p>Connect or create your account</p>
      </div>

      <div>
        <Link to="/login/user" style={buttonStyle}>I'm User</Link>
        <Link to="/login/farmer" style={buttonStyle}>I'm Farmer</Link>
      </div>
    </div>
  );
}

export default HomePage;
