import React, { useState, useEffect, useMemo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { farmerService } from '../services/api';
import authService from '../services/authService';
import './FarmerPage.css';

function FarmerPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [farmer, setFarmer] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [currentUser, setCurrentUser] = useState(null);

  // Déplacer useMemo au début, avant les useEffect
  const shouldShowButtons = useMemo(() => {
    return currentUser && farmer && currentUser.id === farmer.user_id;
  }, [currentUser, farmer]);

  useEffect(() => {
    const user = authService.getCurrentUser();
    console.log('CurrentUser après récupération:', user);
    setCurrentUser(user);
  }, []);

  useEffect(() => {
    const fetchFarmer = async () => {
      if (!id) return;
      setLoading(true);
      try {
        const farmerData = await farmerService.getFarmer(id);
        console.log('Données du farmer:', farmerData);
        setFarmer(farmerData);
        setError('');
      } catch (error) {
        console.error('Erreur lors du chargement:', error);
        setError('Erreur lors du chargement des données');
      } finally {
        setLoading(false);
      }
    };

    fetchFarmer();
  }, [id]);

  const handleDelete = async (farmerId) => {
    if (window.confirm('Êtes-vous sûr de vouloir supprimer cette ferme ?')) {
      try {
        await farmerService.deleteFarmer(farmerId);
        navigate('/');
      } catch (error) {
        console.error('Erreur lors de la suppression:', error);
        setError('Erreur lors de la suppression de la ferme');
      }
    }
  };

  if (loading) return <div>Chargement...</div>;
  if (error) return <div>Erreur: {error}</div>;
  if (!farmer) return <div>Aucune donnée trouvée</div>;

  return (
    <div className="farmer-page">
      <div className="farmer-header">
        {farmer.image_url ? (
          <div className="farmer-image-container">
            <img
              src={farmer.image_url}
              alt={`Ferme ${farmer.name}`}
              onLoad={() => console.log('Image chargée avec succès')}
              onError={(e) => {
                console.error('Erreur de chargement de l\'image');
                console.log('URL de l\'image:', farmer.image_url);
                e.target.style.display = 'none';
              }}
            />
          </div>
        ) : (
          <div className="farmer-image-placeholder" />
        )}
        <h1>{farmer.name}</h1>
      </div>

      <div className="farmer-details">
        <p><strong>Description:</strong> {farmer.description}</p>
        <p><strong>Adresse:</strong> {farmer.address}, {farmer.city} {farmer.zip_code}</p>
        <p><strong>Téléphone:</strong> {farmer.phone}</p>
        {farmer.website && (
          <p><strong>Site web:</strong> <a href={farmer.website} target="_blank" rel="noopener noreferrer">{farmer.website}</a></p>
        )}
      </div>

      {shouldShowButtons && (
        <div className="farmer-actions">
          <button onClick={() => navigate(`/farmers/${id}/edit`)}>Modifier</button>
          <button onClick={() => handleDelete(id)}>Supprimer</button>
        </div>
      )}
    </div>
  );
}

export default FarmerPage;
