import React, { useState, useEffect } from 'react';
import { useNavigate, useParams, Link, useLocation } from 'react-router-dom';
import { userService } from '../services/api'; // Importez userService au lieu d'axios
import './LoginForm.css';

function LoginForm() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();
  const { type } = useParams();
  const location = useLocation();

  useEffect(() => {
    if (location.state && location.state.successMessage) {
      setSuccessMessage(location.state.successMessage);
    }
  }, [location]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      // Utilisez userService.login au lieu d'axios.post
      await userService.login({ username, password });
      // Le token et les informations utilisateur sont déjà stockés dans la fonction login du service
      // Redirigez l'utilisateur vers la page d'accueil
      navigate('/');
    } catch (error) {
      console.error('Erreur de connexion', error);
      if (error.response) {
        setError(error.response.data);
      } else if (error.request) {
        setError('Aucune réponse du serveur.');
      } else {
        setError('Erreur lors de la connexion.');
      }
    }
  };

  return (
    <div>
      <h2>Connexion {type === 'farmer' ? 'Agriculteur' : 'Utilisateur'}</h2>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      {successMessage && <div style={{ color: 'green' }}>{successMessage}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Nom d'utilisateur :</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Mot de passe :</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Se connecter</button>
      </form>
      <p>
        Pas encore de compte ?{' '}
        <Link to={type === 'farmer' ? '/register/farmer' : '/register'}>
          Créer un compte
        </Link>
      </p>
    </div>
  );
}

export default LoginForm;
