import React, { useState } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function LoginForm() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const { type } = useParams(); // Récupère le paramètre "type" de l'URL
  const navigate = useNavigate(); // Hook pour la redirection

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Envoie les données de connexion à l'API back-end
      const response = await axios.post('/users/login', { username, password });

      // Gère la réponse (par exemple, stocke le token JWT, redirige l'utilisateur)
      console.log('Connexion réussie', response.data);

      // Redirige l'utilisateur en fonction de son type (user ou farmer)
      if (type === 'user') {
        navigate('/'); // Redirige vers la page d'accueil
      } else if (type === 'farmer') {
        navigate('/farmers/profile'); // Redirige vers le profil de l'agriculteur (à créer)
      }
    } catch (error) {
      console.error('Erreur lors de la connexion', error);
    }
  };

  return (
    <div>
      <h1>Connexion ({type === 'user' ? 'Utilisateur' : 'Agriculteur'})</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Nom d'utilisateur:
          <input type="text" value={username} onChange={e => setUsername(e.target.value)} />
        </label>
        <label>
          Mot de passe:
          <input type="password" value={password} onChange={e => setPassword(e.target.value)} />
        </label>
        <button type="submit">Se connecter</button>

        {/* Lien vers le formulaire d'inscription approprié */}
        {type === 'user' ? (
          <Link to="/register">Pas de compte ? S'inscrire</Link>
        ) : (
          <Link to="/register/farmer">Pas de compte ? S'inscrire comme agriculteur</Link>
        )}
      </form>
    </div>
  );
}

export default LoginForm;
