import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import logo from '../assets/logo.png';

function HomePage() {
  const [farmers, setFarmers] = useState([]);
  const [city, setCity] = useState('');
  const [useGeolocation, setUseGeolocation] = useState(false);
  const [radius, setRadius] = useState('');

  useEffect(() => {
    // Récupérer la liste des producteurs depuis l'API back-end
    axios.get('/farmers')
      .then(response => {
        setFarmers(response.data);
      })
      .catch(error => {
        console.error('Erreur lors de la récupération des producteurs', error);
      });
  }, []);

  const buttonStyle = {
    padding: '10px 20px',
    border: '1px solid black',
    borderRadius: '5px',
    cursor: 'pointer',
    textDecoration: 'none',
    color: 'black',
    backgroundColor: 'white',
    margin: '10px'
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <img src={logo} alt="Logo Save Agri" style={{ width: '200px' }} />
      <div>
        <label htmlFor="city">Enter a city:</label>
        <input
          type="text"
          id="city"
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
      </div>
      <div>
        <label htmlFor="useGeolocation">or located:</label>
        <input
          type="checkbox"
          id="useGeolocation"
          checked={useGeolocation}
          onChange={(e) => setUseGeolocation(e.target.checked)}
        />
      </div>
      <div>
        <label htmlFor="radius">Enter the radius:</label>
        <input
          type="number"
          id="radius"
          value={radius}
          onChange={(e) => setRadius(e.target.value)}
        />
      </div>
      <p>Connect or create your account</p>
      <div>
        <Link to="/login/user" style={buttonStyle}>I'm User</Link>
        <Link to="/login/farmer" style={buttonStyle}>I'm Farmer</Link>
      </div>
    </div>
  );
}

export default HomePage;
