import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import ProductList from './ProductList';
import axios from 'axios';
import './FarmerPage.css';

function FarmerPage() {
  const { id } = useParams();
  const [farmer, setFarmer] = useState(null);

  useEffect(() => {
    // Récupérer les détails du producteur depuis l'API back-end
    axios.get(`/farmers/${id}`)
      .then(response => {
        setFarmer(response.data);
      })
      .catch(error => {
        console.error('Erreur lors de la récupération du producteur', error);
      });
  }, [id]);

  if (!farmer) {
    return <div>Chargement...</div>;
  }

  return (
    <div className="farmer-page">
      <h1>Profil Agriculteur</h1>
      <div className="farmer-details">
        <h2>{farmer.name}</h2>
        <p><strong>Description :</strong> {farmer.description}</p>
        <p><strong>Adresse :</strong> {farmer.address}</p>
        <p><strong>Ville :</strong> {farmer.city}</p>
        <p><strong>Code Postal :</strong> {farmer.zip_code}</p>
        <p><strong>Téléphone :</strong> {farmer.phone}</p>
        <p><strong>Site Web :</strong> {farmer.website || 'Non renseigné'}</p>
      </div>
      <h2>Produits</h2>
      <ProductList farmerId={id} />
      <Link to="/" className="home-button">Retour à l'accueil</Link>
    </div>
  );
}

export default FarmerPage;
