import React, { useState, useEffect } from 'react';
import { useNavigate, useParams, Link, useLocation } from 'react-router-dom';
import axios from 'axios';

function LoginForm() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState(''); // Ajout d'un état pour le message de succès
  const navigate = useNavigate();
  const { type } = useParams(); // 'user' ou 'farmer'
  const location = useLocation();

  useEffect(() => {
    // Vérifie s'il y a un message de succès dans l'état de la localisation
    if (location.state && location.state.successMessage) {
      setSuccessMessage(location.state.successMessage);
    }
  }, [location]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const API_URL = process.env.REACT_APP_API_URL;
      const response = await axios.post('http://localhost:3000/users/login', { username, password });
      // Stockez le token JWT reçu du backend
      localStorage.setItem('token', response.data.token);
      // Stockez également le nom d'utilisateur
      localStorage.setItem('user', JSON.stringify({ username: username }));
      // Redirigez l'utilisateur vers la page d'accueil
      navigate('/');
    } catch (error) {
      console.error('Erreur de connexion', error);
      // Gérer les différentes erreurs possibles
      if (error.response) {
        // Le serveur a répondu avec un code d'erreur
        setError(error.response.data);
      } else if (error.request) {
        // La requête n'a pas reçu de réponse
        setError('Aucune réponse du serveur.');
      } else {
        // Erreur lors de la configuration de la requête
        setError('Erreur lors de la connexion.');
      }
    }
  };

  return (
    <div>
      <h2>Connexion {type === 'farmer' ? 'Agriculteur' : 'Utilisateur'}</h2>
      {error && <div style={{ color: 'red' }}>{error}</div>}
      {successMessage && <div style={{ color: 'green' }}>{successMessage}</div>} {/* Afficher le message de succès */}
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Nom d'utilisateur :</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Mot de passe :</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Se connecter</button>
      </form>
      <p>
        Pas encore de compte ?{' '}
        <Link to={type === 'farmer' ? '/register/farmer' : '/register'}>
          Créer un compte
        </Link>
      </p>
    </div>
  );
}

export default LoginForm;
