import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { farmerService } from '../services/api';
import authService from '../services/authService';

function UpdateFarmerForm() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [formData, setFormData] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Vérifier l'authentification
  useEffect(() => {
    const user = authService.getCurrentUser();
    if (!user) {
      navigate('/login');
    }
  }, [navigate]);

  // Charger les données du farmer
  useEffect(() => {
    const fetchFarmer = async () => {
      try {
        const response = await farmerService.getFarmer(id);
        const farmerData = response.data;

        // Vérifier si l'utilisateur actuel est le propriétaire
        const currentUser = JSON.parse(sessionStorage.getItem('user'));
        if (currentUser.id !== farmerData.user_id) {
          navigate('/');
          return;
        }

        setFormData(farmerData);
        setLoading(false);
      } catch (error) {
        setError('Erreur lors du chargement des données');
        setLoading(false);
      }
    };

    fetchFarmer();
  }, [id, navigate]);

  if (loading) return <div>Chargement...</div>;
  if (!formData) return <div>Erreur de chargement des données</div>;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      await farmerService.updateFarmer(id, formData);
      setSuccess('Modifications enregistrées avec succès !');
      setTimeout(() => navigate(`/farmer/${id}`), 2000);
    } catch (error) {
      setError('Erreur lors de la mise à jour');
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
      <h2 style={{ color: '#4CAF50', marginBottom: '20px' }}>Modifier les informations</h2>

      {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
      {success && <div style={{ color: 'green', marginBottom: '10px' }}>{success}</div>}

      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '15px' }}>
          <label style={{ display: 'block', marginBottom: '5px' }}>Nom :</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            style={{ width: '100%', padding: '8px' }}
            required
          />
        </div>

        {/* Autres champs du formulaire... */}

        <button
          type="submit"
          style={{
            backgroundColor: '#4CAF50',
            color: 'white',
            padding: '10px 20px',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Enregistrer les modifications
        </button>
      </form>
    </div>
  );
}

export default UpdateFarmerForm;
