import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import axios from 'axios';

function RegisterForm() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const [error, setError] = useState(''); // Ajout d'un état pour les erreurs
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); // Réinitialiser l'erreur
    try {
      const response = await axios.post('/users', { username, password, email });
      // Inscription réussie : rediriger vers la page de connexion avec un message de succès
      navigate('/login/user', { state: { successMessage: 'Compte créé avec succès. Veuillez vous connecter.' } });
    } catch (error) {
      console.error('Erreur lors de l\'inscription', error);
      // Gérer les différentes erreurs possibles
      if (error.response) {
        // Le serveur a répondu avec un code d'erreur
        setError(error.response.data.message || 'Erreur lors de l\'inscription.');
      } else if (error.request) {
        // La requête n'a pas reçu de réponse
        setError('Aucune réponse du serveur.');
      } else {
        // Erreur lors de la configuration de la requête
        setError('Erreur lors de l\'inscription.');
      }
    }
  };

  return (
    <div>
      <h2>Inscription Utilisateur</h2>
      {error && <div style={{ color: 'red' }}>{error}</div>} {/* Afficher l'erreur */}
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Nom d'utilisateur :</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Mot de passe :</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="email">Email :</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <button type="submit">S'inscrire</button>
      </form>
      <p>
        Déjà un compte ? <Link to="/login/user">Se connecter</Link>
      </p>
    </div>
  );
}

export default RegisterForm;
