import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './RegisterFarmerForm.css'; // Assurez-vous de créer ce fichier CSS

function RegisterFarmerForm() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    username: '',
    password: '',
    email: '',
    name: '',
    description: '',
    address: '',
    city: '',
    zipCode: '',
    phone: '',
    website: '',
    photo: null
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: files ? files[0] : value
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError('');
    setSuccess('');

    try {
      // Envoie les données d'inscription à l'API back-end
      const userResponse = await axios.post('/users', {
        username: formData.username,
        password: formData.password,
        email: formData.email
      });

      const userId = userResponse.data.user.id;

      // Création du FormData pour l'envoi de fichier
      const farmerFormData = new FormData();
      Object.keys(formData).forEach(key => {
        if (key !== 'username' && key !== 'password' && key !== 'email') {
          farmerFormData.append(key, formData[key]);
        }
      });
      farmerFormData.append('userId', userId);

      // Création du profil agriculteur
      await axios.post('/farmers', farmerFormData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setSuccess('Inscription réussie !');
      setTimeout(() => navigate('/dashboard'), 2000); // Redirection après 2 secondes
    } catch (error) {
      setError("Erreur lors de l'inscription: " + error.response?.data?.message || error.message);
    }
  };

  return (
    <div className="register-farmer-container">
      <h1>Inscription Agriculteur</h1>
      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}
      <form onSubmit={handleSubmit} className="register-farmer-form">
        <div className="form-section">
          <h2>Informations de Compte</h2>
          <input type="text" name="username" value={formData.username} onChange={handleChange} placeholder="Nom d'utilisateur" required />
          <input type="password" name="password" value={formData.password} onChange={handleChange} placeholder="Mot de passe" required />
          <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Email" required />
        </div>

        <div className="form-section">
          <h2>Informations de la Ferme</h2>
          <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Nom de la ferme" required />
          <textarea name="description" value={formData.description} onChange={handleChange} placeholder="Description de la ferme" required />
          <input type="text" name="address" value={formData.address} onChange={handleChange} placeholder="Adresse" required />
          <input type="text" name="city" value={formData.city} onChange={handleChange} placeholder="Ville" required />
          <input type="text" name="zipCode" value={formData.zipCode} onChange={handleChange} placeholder="Code Postal" required />
          <input type="tel" name="phone" value={formData.phone} onChange={handleChange} placeholder="Téléphone" required />
          <input type="url" name="website" value={formData.website} onChange={handleChange} placeholder="Site Web" />
          <input type="file" name="photo" onChange={handleChange} accept="image/*" required />
        </div>

        <button type="submit" className="submit-button">S'inscrire comme agriculteur</button>
      </form>
    </div>
  );
}

export default RegisterFarmerForm;
