import React, { useState } from 'react';
import { farmerService } from '../services/api';

function UpdateFarmerForm({ farmer, onUpdate }) {
  const [formData, setFormData] = useState({
    name: farmer.name,
    description: farmer.description,
    address: farmer.address,
    city: farmer.city,
    zip_code: farmer.zip_code,
    phone: farmer.phone,
    website: farmer.website
  });
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const updatedFarmer = await farmerService.updateFarmer(farmer.id, formData);
      onUpdate(updatedFarmer);
    } catch (error) {
      setError('Erreur lors de la mise à jour');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="update-form">
      {error && <div className="error-message">{error}</div>}

      <div>
        <label>Nom :</label>
        <input
          type="text"
          name="name"
          value={formData.name}
          onChange={handleChange}
          required
        />
      </div>

      <div>
        <label>Description :</label>
        <textarea
          name="description"
          value={formData.description}
          onChange={handleChange}
          required
        />
      </div>

      {/* Ajoutez les autres champs de la même manière */}

      <button type="submit">Enregistrer les modifications</button>
    </form>
  );
}

export default UpdateFarmerForm;
